const setLimitationError=t=>{console.error(`Переданный формат ограничения(data-limitation="${t}") - не поддерживается. Проверьте корректность введённых значений.`)};export const getLimitationsRegEx=t=>{switch(t){case"digit":return/[^\d]/g;case"name":return/[^a-zA-Zа-яёА-ЯЁ\-\s]/g;case"letters":return/[^a-zA-Zа-яёА-ЯЁ\s]/g;case"letters-and-digit":return/[^a-zA-Zа-яёА-ЯЁ\s\d]/g;case"cyrillic":return/[^а-яёА-ЯЁ\s]/g;case"latin":return/[^a-zA-Z\s]/g;default:return setLimitationError(t)}};export const getMatrixLimitationsRegEx=t=>{switch(t){case"digit":return/[^\d]/g;case"name":return/[^\а-яё\А-ЯЁ\a-z\A-Z\-]]/g;case"letters":return/[^\а-яё\А-ЯЁ\a-z\A-Z]/g;case"letters-and-digit":return/[^\а-яё\А-ЯЁ\a-z\A-Z\d]/g;case"cyrillic":return/[^\а-яё\А-ЯЁ]/g;case"latin":return/[^\a-z\A-Z]/g;default:return!1}};export const getMailRegEx=()=>/[a-zA-Zа-яёА-ЯЁ0-9]{1}([a-zA-Zа-яёА-ЯЁ0-9\-_\.]{1,})?@[a-zA-Zа-яёА-ЯЁ0-9\-]{1}([a-zA-Zа-яёА-ЯЁ0-9.\-]{1,})?[a-zA-Zа-яёА-ЯЁ0-9\-]{1}\.[a-zA-Zа-яёА-ЯЁ]{2,6}/;