import{bytesToSize}from"./helper.js";export const createPreviewMarkup=(e,i,n,a)=>{const t=e.name.split("."),p=t[t.length-1];return`<div class="input-upload__preview-item${a?" is-invalid":""}">\n            <button class="input-upload__preview-item-remove" type="button" data-file-name="${e.name}">&times;</button>\n            ${n.previewImg?`<img class="input-upload__preview-img" src="${i.target.result}" alt="${e.name}" />`:""}\n            ${n.iconFormat?`<img class="input-upload__preview-icon" src="${n.iconFormat[p]?n.iconFormat[p]:n.iconFormat.default}" alt="" />`:""}\n            ${n.fileInfo?`<div class="input-upload__preview-file-info"" />\n              ${n.fileInfo.fileName?`<span class="input-upload__preview-file-name"" />\n                ${e.name}\n              </span>`:""}\n              ${n.fileInfo.fileSize?`<span class="input-upload__preview-file-size"" />\n                ${bytesToSize(e.size)}\n              </span>`:""}\n            </div>`:""}\n          </div>`};