const createNativeOptionsMarkup=(e,t)=>e.map(((e,i)=>{if(t.length){return t.find((e=>e===i))===i?`<option ${e.value?`value=${e.value}`:""} selected>${e.text?`${e.text}`:""}</option>`:`<option ${e.value?`value=${e.value}`:""}>${e.text?`${e.text}`:""}</option>`}return`<option ${e.value?`value=${e.value}`:""}>${e.text?`${e.text}`:""}</option>`})).join("\n");export const createNativeSelectMarkup=({id:e,items:t,multiple:i,name:a,required:n,activeIndex:o=[]})=>`<select ${e?`id='${e}'`:""} ${a?`name='${a}'`:""} ${i?"multiple":""} ${n?"required":""} tabindex="-1" aria-hidden="true">\n            <option value=""></option>\n            ${createNativeOptionsMarkup(t,o)}\n          </select>`;